mapper.select:
SELECT ROW_NUMBER() over (ORDER BY C.DANGER DESC, D.WARN DESC, G.CON DESC, A.SNSRID) AS ROWNUM , E.STRCODE , E.STRNAME , A.SNSRID , TRIM(LEADING '0' FROM A.SNSRID) AS SNSRIDTRIM , A.SNSRNICK , IFNULL(C.DANGER, 0) AS DANGER , IFNULL(D.WARN, 0) AS WARN , IFNULL(G.CON, 0) AS CON , IFNULL(F.CHK, 0) AS CHK FROM F_SENSOR_INFO A LEFT JOIN (SELECT * FROM F_LEVEL_AREA WHERE AREALEVEL = 1 AND DELYN = 'N') B ON A.AREACODE = B.AREACODE LEFT JOIN F_STORE E ON A.AREACODE = E.AREACODE AND A.STRCODE = E.STRCODE LEFT JOIN (SELECT SNSRID, COUNT(*) AS DANGER FROM F_SENSOR_LOG WHERE REGDATE > CURDATE() AND INSTR(CONCAT(OCALM, IGOALM, IGRALM), '2') > 0 GROUP BY SNSRID ) C ON A.SNSRID = C.SNSRID LEFT JOIN (SELECT SNSRID, COUNT(*) AS WARN FROM F_SENSOR_LOG WHERE REGDATE > CURDATE() AND INSTR(CONCAT(OCALM, IGOALM, IGRALM), '1') > 0 GROUP BY SNSRID ) D ON A.SNSRID = D.SNSRID LEFT JOIN (SELECT SNSRID, COUNT(*) AS CHK FROM F_SENSOR_CHECK WHERE CHECKSTAT = 'ING' GROUP BY SNSRID ) F ON A.SNSRID = F.SNSRID LEFT JOIN (SELECT SNSRID, COUNT(*) AS CON FROM F_SENSOR_DATA WHERE SNSRRCVTIME > NOW() - INTERVAL 1 HOUR GROUP BY SNSRID ) G ON A.SNSRID = G.SNSRID WHERE A.DELYN != 'Y' AND B.GUCODE LIKE '30%' AND A.AREACODE = #{areacode}
AND A.STRCODE = #{strcode}
ORDER BY C.DANGER DESC, D.WARN DESC, G.CON DESC, A.SNSRID
