SELECT A.CNT + B.CNT + C.CNT FROM (SELECT COUNT(L.REGDATE) AS CNT FROM (SELECT SNSRID, REGDATE FROM F_SENSOR_LOG WHERE REGDATE <= CONCAT(#{regdt}, ' 23:59:59') AND SNSRID = #{snsrid} AND (OCALM + IGOALM + IGRALM) > 0 INSTR(CONCAT(OCALM, IGOALM, IGRALM), '2') > 0
(INSTR(CONCAT(OCALM, IGOALM, IGRALM), '2') = 0 AND INSTR(CONCAT(OCALM, IGOALM, IGRALM), '1') > 0)
) L LEFT JOIN F_SENSOR_INFO I ON L.SNSRID = I.SNSRID LEFT JOIN F_STORE S ON I.STRCODE = S.STRCODE WHERE CONCAT(S.USEYN, S.DELYN, S.GRPCODE) = 'YN042' AND S.STRCODE != 'FS_STR_0000000001859' AND CONCAT(I.DELYN, I.STRCODE) LIKE 'NFS_STR_%' ) A , (SELECT COUNT(L.REGDATE) AS CNT FROM (SELECT SNSRID, REGDATE FROM F_SENSOR_LOG_2021 WHERE REGDATE <= CONCAT(#{regdt}, ' 23:59:59') AND SNSRID = #{snsrid} AND (OCALM + IGOALM + IGRALM) > 0 INSTR(CONCAT(OCALM, IGOALM, IGRALM), '2') > 0
(INSTR(CONCAT(OCALM, IGOALM, IGRALM), '2') = 0 AND INSTR(CONCAT(OCALM, IGOALM, IGRALM), '1') > 0)
) L LEFT JOIN F_SENSOR_INFO I ON L.SNSRID = I.SNSRID LEFT JOIN F_STORE S ON I.STRCODE = S.STRCODE WHERE CONCAT(S.USEYN, S.DELYN, S.GRPCODE) = 'YN042' AND S.STRCODE != 'FS_STR_0000000001859' AND CONCAT(I.DELYN, I.STRCODE) LIKE 'NFS_STR_%' ) B , (SELECT COUNT(L.REGDATE) AS CNT FROM (SELECT SNSRID, REGDATE FROM F_SENSOR_LOG_2020 WHERE REGDATE <= CONCAT(#{regdt}, ' 23:59:59') AND SNSRID = #{snsrid} AND (OCALM + IGOALM + IGRALM) > 0 INSTR(CONCAT(OCALM, IGOALM, IGRALM), '2') > 0
(INSTR(CONCAT(OCALM, IGOALM, IGRALM), '2') = 0 AND INSTR(CONCAT(OCALM, IGOALM, IGRALM), '1') > 0)
) L LEFT JOIN F_SENSOR_INFO I ON L.SNSRID = I.SNSRID LEFT JOIN F_STORE S ON I.STRCODE = S.STRCODE WHERE CONCAT(S.USEYN, S.DELYN, S.GRPCODE) = 'YN042' AND S.STRCODE != 'FS_STR_0000000001859' AND CONCAT(I.DELYN, I.STRCODE) LIKE 'NFS_STR_%' ) C
