mapper.select:
(SELECT 1 AS LEVEL , S.GUCODE AS CODE , S.GUNAME AS NAME , S.SENSOR , S.STORE , S.DANGER , S.WARN , S.DISCON , NULL AS UPCODE , S.DANGER * 10000 + S.WARN AS ORDERNUM FROM
)
UNION
(SELECT 2 AS LEVEL , S.AREACODE AS CODE , S.AREANAME AS NAME , S.SENSOR , S.STORE , S.DANGER , S.WARN , S.DISCON , S.GUCODE AS UPCODE , 0 - (S.DANGER * 10000 + S.WARN) AS ORDERNUM FROM
) ORDER BY 1, 10 DESC
