mapper.select:
(SELECT 1 AS LEVEL , S.GUCODE AS CODE , '인천' AS NAME , S.SENSOR , S.STORE , S.DANGER , S.WARN , S.DISCON , NULL AS UPCODE , S.DANGER * 10000 + S.WARN AS ORDERNUM FROM
)
UNION
(SELECT 2 AS LEVEL , S.AREACODE AS CODE , '종합비즈니스센터' AS NAME , S.SENSOR , S.STORE , S.DANGER , S.WARN , S.DISCON , S.GUCODE AS UPCODE , 0 - (S.DANGER * 10000 + S.WARN) AS ORDERNUM FROM
) ORDER BY LEVEL, ORDERNUM DESC
