mapper.select:
SELECT STR_TO_DATE(DATE_FORMAT(C.SNSRRCVTIME,'%Y-%m'),'%Y-%m-%d') AS REGDT, DATE(C.SNSRRCVTIME) AS REGDT, DATE_FORMAT(C.SNSRRCVTIME,'%Y-%m-%d %H') AS REGDT, C.SNSRRCVTIME AS REGDT,
AVG(C.SNSRKWH) AS KWH , AVG(C.SNSRWATT) AS WATT , AVG(C.SNSRVOLT) AS VOLT , AVG(C.SNSRAMPERE) AS AMPERE , AVG(C.SNSRHZ) AS HZ , AVG(C.SNSRPF) AS PF , AVG(C.SNSRIGO) AS IGO , AVG(C.SNSRIGR) AS IGR , AVG(C.SNSRIGC) AS IGC , AVG(C.SNSRREG) AS REG FROM F_SENSOR_INFO A LEFT JOIN F_STORE B ON A.STRCODE = B.STRCODE LEFT JOIN ${tblSensorData} C ON A.SNSRID = C.SNSRID WHERE B.STRCODE = #{strcode} AND A.SNSRID = #{snsrid}
AND C.SNSRRCVTIME BETWEEN CONCAT(DATE(#{regdt}), ' 00:00:00') AND CONCAT(LAST_DAY(DATE(#{regdt}), ' 23:59:59'))
AND C.SNSRRCVTIME BETWEEN CONCAT(DATE(#{regdt}), ' 00:00:00') AND CONCAT(DATE(#{regdt}), ' 23:59:59')
GROUP BY 1 ORDER BY 1 ${order}
