mapper.insert:
INSERT INTO F_AREA_IMG (
      IMGLEVEL
    , AREACODE
    , STRCODE
    , IMGNAME
    , IMGSIZE
    , IMGPATH
    , IMGNUM
    , ORINAME
    , REGDATE
) VALUES (
      'store'
    , #{areaCode}
    , #{strCode}
    , #{imgName}
    , #{imgSize}
    , #{imgPath}
    , #{imgNum}
    , #{oriName}
    , NOW()
)

mapper.delete:
DELETE FROM F_AREA_IMG
      WHERE AREACODE = #{areaCode}
        AND STRCODE  = #{strCode}
        AND IMGNAME  = #{imgName}
