mapper.select:
SELECT INSPID AS inspId , INSPNAME As inspName , INSPEMAIL AS inspEmail , INSPTEL AS inspTel , INSPMOBILE AS inspMobile , INSPAREACODE AS inspAreaCode , INSPSHOPNAME AS inspShopName FROM F_INSPECTOR WHERE USEYN = #{useYn} AND ALARMUSE = #{alarmUse} AND LOGINFAILCNT >= 5
AND ( INSPID LIKE CONCAT('%', #{searchWrd}, '%') OR INSPNAME LIKE CONCAT('%', #{searchWrd}, '%') )
AND INSPAREACODE = #{inspAreaCode} ORDER BY REGDATE DESC LIMIT #{page}, #{sizePerPage}

SELECT COUNT(INSPID)
  FROM F_INSPECTOR
 WHERE USEYN = #{useYn}
   AND ALARMUSE = #{alarmUse}
   AND LOGINFAILCNT >= 5

   AND (    INSPID LIKE CONCAT('%', #{searchWrd}, '%')
         OR INSPNAME LIKE CONCAT('%', #{searchWrd}, '%') )

   AND INSPAREACODE = #{inspAreaCode}

SELECT COUNT(INSPID)
  FROM F_INSPECTOR
 WHERE USEYN = #{inspId}

mapper.insert:
INSERT INTO F_INSPECTOR (
            INSPID
          , INSPPASS
          , INSPNAME
          , INSPEMAIL
          , INSPTEL
          , INSPMOBILE
          , INSPAREACODE
          , INSPSHOPNAME
          , INSPADDR
          , ALARMUSE
          , USEYN
          , REGDATE
          , INSPRSNDATE
          , LOGINFAILCNT
) VALUES (
            #{inspId}
          , #{inspPass}
          , #{inspName}
          , #{inspEmail}
          , #{inspTel}
          , #{inspMobile}
          , #{inspAreaCode}
          , #{inspShopName}
          , #{inspAddr}
          , 'Y'
          , #{useYn}
          , NOW()
          , NOW()
          , 0
)
