mapper.select:
SELECT COUNT(*)
FROM F_MEMBER
WHERE USERID = #{userId}

mapper.insert:
INSERT INTO F_MEMBER (
            USERID
          , MEMPWD
          , MEMNAME
          , MEMEMAIL
          , MEMTEL
          , MEMMOBILE
          , MEMLEVEL
          , MEMRCVSMS
          , MEMRCVPUSH
          , REGDATE
          , MEMRSNTDATE
          , MEMISLEAVE
          , USEYN
          , MEMMEMO
          , PWCHANGE
          , GROUPUSE
) VALUES (
            #{ userId      }
          , #{ memPwd      }
          , #{ memName     }
          , #{ memEmail    }
          , #{ memTel      }
          , #{ memMobile   }
          , 0
          , #{ memRcvSms   }
          , #{ memRcvPush  }
          , NOW()
          , NOW()
          , #{ memIsLeave }
          , #{ useYn }
          , #{ memMemo }
          , 'Y'
          , #{ groupUse }
         )
