mapper.select:
SELECT PUSHSEQ AS pushSeq , SNSRSEQ AS snsrSeq , PUSHMSG AS pushMsg , DATE_FORMAT(PUSHSTIME, '%Y-%m-%d %H:%i:%s') AS pushStime , USERID AS userId , PUSHRSLT AS pushRslt , RESENDSEQ AS resendSeq , TOINFO AS toInfo , FROMINFO AS fromInfo , SENDRESULT AS sendResult FROM F_PUSH_LOG WHERE PUSHSTIME BETWEEN CONCAT(#{startDate}, ' 00:00:00') AND CONCAT(#{endDate}, ' 23:59:59') AND PUSHRSLT = #{isSuccess}
AND ( PUSHMSG LIKE CONCAT('%', #{searchWrd}, '%') OR SNSRSEQ LIKE CONCAT('%', #{searchWrd}, '%') )
ORDER BY PUSHSEQ DESC LIMIT #{page}, #{sizePerPage}

SELECT SMSSEQ AS smsSeq , SNSRSEQ AS snsrSeq , SMSMSG AS smsMsg , DATE_FORMAT(SMSSTIME, '%Y-%m-%d %H:%i:%s') AS smsStime , USERID AS userId , SMSRCV AS smsRcv , SMSLEVEL AS smsLevel , SMSRSLT AS smsRslt , SMSTYPE AS smsType , SMSLEVEL AS smsLevel , RESENDSEQ AS resendSeq , SNSRID AS snsrId FROM F_SMS_LOG WHERE SMSSTIME BETWEEN CONCAT(#{startDate}, ' 00:00:00') AND CONCAT(#{endDate}, ' 23:59:59') AND SMSRSLT = #{isSuccess}
AND ( SMSMSG LIKE CONCAT('%', #{searchWrd}, '%') OR SMSSEQ LIKE CONCAT('%', #{searchWrd}, '%') )
ORDER BY SMSSEQ desc LIMIT #{page}, #{sizePerPage}
