mapper.select:
SELECT COUNT(*) FROM F_SENSOR_INFO SI LEFT JOIN F_STORE ST ON ST.STRCODE = SI.STRCODE AND ST.AREACODE = SI.AREACODE WHERE SI.DELYN = 'N'
AND SI.AREACODE = #{areaCode}
AND SI.LEVELAREACODE = #{levelAreaCode}
AND ( SI.SNSRID LIKE CONCAT( '%', #{ searchWrd }, '%' ) OR SI.SNSRNICK LIKE CONCAT( '%', #{ searchWrd }, '%' ) OR ST.STRNAME LIKE CONCAT( '%', #{ searchWrd }, '%' ) OR SI.SNSRSEQ LIKE CONCAT( '%', #{ searchWrd }, '%' ) )

SELECT Z.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS rownum , A.* FROM ( SELECT SI.SNSRSEQ AS snsrSeq , SI.SNSRID AS snsrId , SI.SNSRNICK AS snsrNick , ST.STRNAME AS strName , DATE_FORMAT( SI.REGDATE, '%Y-%m-%d %H:%i' ) AS regDate , DATE_FORMAT( SI.UPDDATE, '%Y-%m-%d %H:%i' ) AS updDate , SI.SNSRID AS snsrIdKey , SI.SOC1V1 AS sOc1V1 , SI.SOC2V1 AS sOc2V1 , SI.SIGR1V AS sIgr1V , SI.SIGR2V AS sIgr2V , SI.SIGO1V AS sIgo1V , SI.SIGO2V AS sIgo2V , SI.CHANNEL AS channel , SI.DELYN AS delYn FROM F_SENSOR_INFO SI LEFT JOIN F_STORE ST ON ST.STRCODE = SI.STRCODE AND ST.AREACODE = SI.AREACODE WHERE SI.DELYN = 'N'
AND SI.AREACODE = #{areaCode}
AND SI.LEVELAREACODE = #{levelAreaCode}
AND ( SI.SNSRID LIKE CONCAT( '%', #{ searchWrd }, '%' ) OR SI.SNSRNICK LIKE CONCAT( '%', #{ searchWrd }, '%' ) OR ST.STRNAME LIKE CONCAT( '%', #{ searchWrd }, '%' ) OR SI.SNSRSEQ LIKE CONCAT( '%', #{ searchWrd }, '%' ) )
ORDER BY SI.SNSRID ) A, ( SELECT @ROWNUM := 0 ) R ORDER BY rownum desc ) Z LIMIT #{ page }, #{ sizePerPage }
